INSERT INTO `sanpham` VALUES ('90', '14', 'Đầu Karaoke chuyên nghiệp Arirang AR-36MD', 'Đọc các loại đĩa , không kén đĩa\r\nĐầy đủ tính năng hát Midi Karaoke\r\nĐọc được các loại file nén Audio và Video\r\nGiao tiếp USB\r\nTính năng chấm điểm bài hát\r\n\r\n1.490.000 VND\r\nĐầu DVD karaoke Arirang AR-36MDlà một phiên bản cao cấp nhất của hãng Arirang được hội tụ nhiều tính năng ưu việt đang được mọi người chú ý và lựa chọn nhiều trên thị trường. Với tính năng âm thanh 5.1 giúp căn phòng nhà bạn tràn ngập âm thanh trong trẻo và sinh động hơn. Ngoài ra, với cổng giao tiếp USB sẽ giúp từng thành viên trong gia đình bạn giải trí với những sở thích riêng như xem phim, nghe nhạc, xem những clip nổi tiếng trên youtobe hay những phim truyện tình lãng mạng trên các trang web nổi tiếng thông qua việc trao đổi dữ liệu thông tin trong USB với đầu DVD. Đặc biệt hơn, gia đình bạn có thể quây quần bên chiếc đầu DVD karaoke Arirang AR-36MD để cùng nhau ca hát với tính năng karaoke chấm điểm thông minh xuất hiện ngay trên màn hình giúp bạn thêm hứng thú với những buổi thi hát hấp dẫn cùng mọi người để cũng nhau giải trí sau một ngày làm việc bận rộn.\r\n\r\nĐẶC ĐIỂM NỔI BẬT\r\nTận hưởng phòng karaoke ngay tại gia đình bạn\r\nVới chiếc đầu DVD karaoke Arirang AR-36MD giúp căn phòng bạn biến thành một phòng karaoke chuyên nghiệp bởi sự tích hợp của đầu karaoke vi tính 5 số và trọn bộ đĩa karaoke Airang giúp bạn lựa chọn bài hát nhanh chóng và phong phú hơn. Ngoài ra, với chức năng karaoke chấm điểm thông minh kết hợp với tính năng hát bè, giờ đây, gia đình bạn sẽ có một không gian của một phòng karaoke hoàn hảo ngay tại nhà mà không cần tốn kém chi phí đi đến những khu vui chơi giải trí như trước.\r\nThiết kế khoa học và tinh tế\r\nChiếc đầu DVD karaoke Arirang AR-36MD được thiết kế khoa học với những nút ấn nổi, các chân cắm của micro và các cổng giao tiếp USB được sắp xếp hợp thời trang tạo cho chiếc đầu karaoke thêm hấp dẫn và bạn có thể dễ dàng thao tác, chọn bài hay kết nối với c