INSERT INTO `sanpham` VALUES ('169', '32', 'Áo khoác kaki 2 lớp nam RINTON', 'Áo khoác nam kaki 2 lớp Riton mang đến các chàng phong cách thời trang phóng khoán, năng động và cá tính.\r\nThiết kế năng động, khóa kéo, tay áo dài có bo, tạo nên phong cách thời trang cá tính, sành điệu.\r\nCho bạn nam phong cách thật ngầu cùng vẻ ngoài vô cùng năng động, khỏe khoắn.\r\nThiết kế năng động, phong cách với chất liệu vải Kaki 2 lớp bền đẹp.\r\nMàu sắc : Xanh rêu\r\nFreesize. Thích hợp nam dưới 70kg.\r\n\r\nKích thước: Dài 68cm, ngực 55cm, Tay 61cm\r\n249.000 VND', null, '29000000', null, '9000000', '2', '2017-05-28 10:57:04', '2017-06-16 10:59:19', '1', '100000', '37', '\0');
