INSERT INTO `sanpham` VALUES ('212', '62', 'Giày thể thao nam Nike Tiempo Genio Leather II (TF) 819216-010', '2.949.000 VND\r\n\r\nChất liệu cao cấp\r\nĐường may chi tiết\r\nThiết kế tinh tế và thời trang\r\n\r\nĐược thành lập năm 1978 dưới bàn tay tài hoa của hai nhà thiết kế Bill Bowerman and Philip Knight, Nike được biết đến là nhà cung cấp quần áo, giày dép và dụng cụ thể thao hàng đầu trên thế giới. Nét đặc sắc của thương hiệu được thể hiện qua slogan “Just do it” với các thiết kế mang tính đầu tư nghiên cứu cao, phù hợp với nhu cầu của người tiêu dùng về chất lượng mẫu mã và mục đích sử dụng. Tất cả các thiết kế của Nike đều cập nhật những màu sắc và xu hướng mới nhất.. Nike khẳng định thương hiệu của mình qua việc sử dụng các chất liệu cao cấp, được thông qua các quy trình sản xuất và kiểm định nghiêm ngặt.\r\n\r\nĐẶC ĐIỂM NỔI BẬT\r\n image Chất liệu cao cấp, thoáng khí cả mặt trong và mặt ngoài giúp bạn luôn dễ chịu và thoải mái dù mang trong thời gian dài\r\n\r\nimage Đường may vô cùng tỉ mỉ, chắc chắn giúp bạn hoàn toàn an tâm về chất lượng sản phẩm\r\n\r\nimage Màu sắc trang nhã, phong cách hiện đại sẽ góp phần khẳng định phong cách thời trang của bạn\r\nLogo thương hiệu được bố trí nổi bật trên sản phẩm.', null, '29000000', null, '9000000', '1', '2017-05-30 10:57:13', '2017-06-16 10:59:19', '2', '100000', '37', '');
