INSERT INTO `sanpham` VALUES ('25', '2', 'Bộ 5 cốc sạc Samsung Galaxy Note 4', 'Sản xuất tại Việt Nam- Với công nghệ sạc nhanh được Samsung giới thiệu cho Galaxy Note 4, củ sạc Galaxy Note 4 cũng ', 'Sản xuất tại Việt Nam- Với công nghệ sạc nhanh được Samsung giới thiệu cho Galaxy Note 4, củ sạc Galaxy Note 4 cũng được thiết kế hoàn toàn khác khi điện áp và dòng đầu ra cũng được chia thành 2 phần: 9.0V - 1.67A ( hỗ trợ sạc nhanh ) và 5.0V - 2.0A\r\n<ul>\r\n<li>- Sản phẩm hỗ trợ công nghệ Fast Charging của Samsung</li>\r\n<li>- Củ sạc bán ra tại shop chúng tôi là hàng chính hãng 100% các bạn có thể tham khảo cách phân biệt dưới đây:</li>\r\n<li>* Củ sạc chính hãng có logo: Adaptive Fast Charging ở mặt sau của sạc</li>\r\n<li>* Củ sạc cho Note 4 có 2 dòng out put như thông số ở treen</li>\r\n<li>* Củ sạc Note 4 được sản xuất tại Việt Nam ( Made in Việt Nam )</li>\r\n<li>* Mạch của củ sạc được làm chi tiết, không như sản phẩm nhái, phải có Silicon gắn trên mạch tránh chập cháy.</li>\r\n<li>* Chân cắm USB bên trong được làm bằng đồng sáng bóng.pin và bộ sạc , pin và bộ sạc Samsung, Samsung</li>\r\n</ul>', '1520000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/B%E1%BB%99%205%20c%E1%BB%91c%20s%E1%BA%A1c%20Samsung%20Galaxy%20Note%204%2F1.jpg?alt=media&token=f3dc243b-8a3f-47f5-a60a-ca1477f2456e', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/B%E1%BB%99%205%20c%E1%BB%91c%20s%E1%BA%A1c%20Samsung%20Galaxy%20Note%204%2Fdock-sac-va-pin-samsung-galaxy-note-4.jpg?alt=media&token=faac4a19-35a5-453e-a2db-b42a771fb717', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/B%E1%BB%99%205%20c%E1%BB%91c%20s%E1%BA%A1c%20Samsung%20Galaxy%20Note%204%2FFast-Charging-of-the-Galaxy-Note-4.jpg?alt=media&token=915d8478-8704-4365-b12e-af4b1a3433b6', '1600000', '4', '2017-06-15 15:50:43', '2017-07-03 15:01:51', null, '100000', '0', '', null);
INSERT INTO `sanpham` VALUES ('27', '2', 'Pin dự phòng Sony S20 - 20.000 mAh', 'Pin lithium ion 20.000 mAh 2 tích hợp Cho phép sạc điện thoại lên đến 8 lần Sạc đến bốn thiết bị cùng lúc với tổng đầu ra ', 'Pin lithium ion 20.000 mAh 2 tích hợp Cho phép sạc điện thoại lên đến 8 lần Sạc đến bốn thiết bị cùng lúc với tổng đầu ra tối đa là 6,9A Tính năng sạc bắc cầu để sạc CP-S20 trong khi sạc các thiết bị của bạn Duy trì được hơn 90% dung lượng sau khoảng 1.000 lần sạc', '2000000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Pin%20d%E1%BB%B1%20ph%C3%B2ng%20Sony%20S20%20-%2020.000%20mAh%2F1.jpg?alt=media&token=712f6201-2a38-4caa-a90f-37ddb994962e', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Pin%20d%E1%BB%B1%20ph%C3%B2ng%20Sony%20S20%20-%2020.000%20mAh%2Fpin-sac-du-phong-sony-cp-s20-2.jpg?alt=media&token=52952142-9a5c-41a8-900b-982f710de799', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Pin%20d%E1%BB%B1%20ph%C3%B2ng%20Sony%20S20%20-%2020.000%20mAh%2Fpin-sac-du-phong-xiaomi-20000-mah-tam-het.png?alt=media&token=8a44f708-0ba2-4cdf-be44-b49a1046b6dd', '2500000', '4', '2017-06-28 15:54:43', '2017-06-30 15:54:49', '8', '100000', '0', '\0', null);
INSERT INTO `sanpham` VALUES ('29', '2', 'Pin sạc dự phòng Romoss Sense mini 5000 mAh', '\r\nROMOSS SENSE MINI\r\n\r\nPin sạc dự phòng Romoss Sense mini 5000mAh là nguồn cung cấp năng lượng hữu dụng cho điện thoại ', '\r\nROMOSS SENSE MINI\r\n\r\nPin sạc dự phòng Romoss Sense mini 5000mAh là nguồn cung cấp năng lượng hữu dụng cho điện thoại của bạn trong những trường hợp khẩn cấp.\r\nRomoss Sense mini có dung lượng pin lớn đến 5000mAh, có thể sạc nhiều lần các dòng điện thoại, máy tính bảng hiện nay.', '1850000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Pin%20s%E1%BA%A1c%20d%E1%BB%B1%20ph%C3%B2ng%20Romoss%20Sense%20mini%205000%20mAh%2F1.jpg?alt=media&token=9fd869e6-7718-43bf-acb4-ee76dfef375c', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Pin%20s%E1%BA%A1c%20d%E1%BB%B1%20ph%C3%B2ng%20Romoss%20Sense%20mini%205000%20mAh%2Fpin-sac-du-phong-romoss-polymos-5-5000mah-01.jpg?alt=media&token=f8fb1cbc-7d3c-4c72-821a-7991db3ddfeb', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Pin%20s%E1%BA%A1c%20d%E1%BB%B1%20ph%C3%B2ng%20Romoss%20Sense%20mini%205000%20mAh%2Fromoss-pin-sac-du-phong-sense-mini-5000-mah-xanh-cap-sac-micro-usb-1m-do-hang-phan-p.jpg?alt=media&token=56825def-cd40-48ef-b8a2-181bd71c9962', '2000000', '10', '2017-06-20 15:57:22', '2017-07-03 15:01:51', null, '100000', '0', '', null);
INSERT INTO `sanpham` VALUES ('30', '3', 'Gimbal Wenpod SP1 thiết bị cân bằng cho Smartphone', 'Cân bằng khung hình cho smartphone khi quay phim \r\nHỗ trợ chụp ảnh xoay 360 độ, panorama \r\nBrushless motor giúp chuyển động mượt mà ', 'Cân bằng khung hình cho smartphone khi quay phim \r\nHỗ trợ chụp ảnh xoay 360 độ, panorama \r\nBrushless motor giúp chuyển động mượt mà \r\nKích thước: 13.5 x 14.5 x 7.4cm ', '5715000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Gimbal%20Wenpod%20SP1%20thi%E1%BA%BFt%20b%E1%BB%8B%20c%C3%A2n%20b%E1%BA%B1ng%20cho%20Smartphone%2F1.jpg?alt=media&token=c17650e7-c0e5-486c-8dcf-bff538b357be', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Gimbal%20Wenpod%20SP1%20thi%E1%BA%BFt%20b%E1%BB%8B%20c%C3%A2n%20b%E1%BA%B1ng%20cho%20Smartphone%2F2.jpg?alt=media&token=8e2f4b14-03a0-4628-a2fe-0697fd047a07', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Gimbal%20Wenpod%20SP1%20thi%E1%BA%BFt%20b%E1%BB%8B%20c%C3%A2n%20b%E1%BA%B1ng%20cho%20Smartphone%2Fax573hh2zlk6v.jpg?alt=media&token=3567815c-1a88-4d74-ba68-fd474c73c1fe', '6000000', '9', '2017-06-20 15:58:09', '2017-07-03 15:01:51', null, '100000', '0', '', null);
INSERT INTO `sanpham` VALUES ('31', '3', 'Ổ cứng di động  Apple Android Mili iData 256GB', 'Mở rộng bộ nhớ của iPhone*; iPad * ( 16/32/64/128/256 GB) và các thiết bị android khác.', 'Mở rộng bộ nhớ của iPhone*; iPad * ( 16/32/64/128/256 GB) và các thiết bị android khác.\r\nLưu trữ tất cả file xử lý trên thiết bị: .doc; .xls; mp4; jpg…\r\nSao lưu và chuyển đổi tất cả các file từ thiết bị này sang thiết bị khác 1 cách đơn giản\r\nBackup và Restore danh bạ\r\nThiết bị duy nhất được Apple chứng nhận sản phẩm ổ cứng di động dùng cho các sản phẩm của Apple.\r\nMili iData là một trong những thiết bị hiếm hoi được Apple chứng nhận là sản phẩm ổ cứng di động dùng cho các sản phẩm của hãng. \r\nMili iData có thiết kế khá nhỏ gọn, vỏ bằng nhôm khối với chất lượng hoàn thiện tốt. Sản phẩm có dung lượng bộ nhớ 16 GB giúp mở rộng bộ nhớ cho các máy iPhone và Android. Ở đầu phía trước là một cổng Lightning để cắm vào các máy chạy iOS. Phía sau là một cổng microUSB để kết nối cáp với máy tính hoặc điện thoại Android. \r\nPhụ kiện đi kèm có một cáp hai đầu đều là microUSB, cáp còn lại có một đầu USB và một đầu microUSB. \r\nCắm trực tiếp Mili iData đọc dữ liệu với iPhone 6. Người dùng cần cài thêm phần mềm riêng do Mili cung cấp để thực việc việc chuyển đổi dữ liệu. \r\nVới các máy Android, người dùng cần sử dụng thêm dây cáp hai đầu microUSB để kết nối. Tuy nhiên, hệ điều hành của Google không đòi hỏi phải cài thêm phần mềm mà chỉ cần dùng sẵn ứng dụng đọc tập tin trong máy. \r\nViệc sao lưu ảnh ra Mili iData và ngược lại không cần qua máy tính, có thể sao lưu nội dung ở mọi nơi. Các máy cũng có thể chơi file nhạc hoặc xem ảnh, văn bản dù vẫn đang lưu ở bộ nhớ ngoài. ', '4190000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/%E1%BB%94%20c%E1%BB%A9ng%20di%20%C4%91%E1%BB%99ng%20%20Apple%20Android%20Mili%20iData%20256GB%2F1.jpg?alt=media&token=f37a3d02-bb79-4ee7-a785-ab5dd166f0e4', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/%E1%BB%94%20c%E1%BB%A9ng%20di%20%C4%91%E1%BB%99ng%20%20Apple%20Android%20Mili%20iData%20256GB%2Fbo-nho-ngoai-mili-idata-64gb-cho-iphone-android-va-may-ti.jpg?alt=media&token=d580ec40-0158-44e9-8369-6e903aa6dc6e', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/%E1%BB%94%20c%E1%BB%A9ng%20di%20%C4%91%E1%BB%99ng%20%20Apple%20Android%20Mili%20iData%20256GB%2Fimage-1851901-63d783c5f20f079723dda39f1b5ac297-product.jpg?alt=media&token=0ebce7f7-de24-4eb3-8c7d-a795d6f12eed', '6000000', '10', '2017-06-21 15:59:26', '2017-06-23 15:59:31', null, '100000', '0', '\0', null);
INSERT INTO `sanpham` VALUES ('32', '3', 'Sạc năng lượng mặt trời Anker PowerPort Solar IQ', 'Thương hiệu số 1 tại Mỹ\r\nNguồn sạc sạch \"miễn phí\" vô tận\r\nCông nghệ sạc nhanh và tối ưu từ Anker', 'Thương hiệu số 1 tại Mỹ\r\nNguồn sạc sạch \"miễn phí\" vô tận\r\nCông nghệ sạc nhanh và tối ưu từ Anker\r\nTính \"di động\" tuyệt đối\r\nThiết kế an toàn', '1999000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/S%E1%BA%A1c%20n%C4%83ng%20l%C6%B0%E1%BB%A3ng%20m%E1%BA%B7t%20tr%E1%BB%9Di%20Anker%20PowerPort%20Solar%20IQ%2F1.jpg?alt=media&token=6f7b07f1-bda7-47e8-9662-b11f5437fb28', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/S%E1%BA%A1c%20n%C4%83ng%20l%C6%B0%E1%BB%A3ng%20m%E1%BA%B7t%20tr%E1%BB%9Di%20Anker%20PowerPort%20Solar%20IQ%2F1.jpg?alt=media&token=6f7b07f1-bda7-47e8-9662-b11f5437fb28', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/S%E1%BA%A1c%20n%C4%83ng%20l%C6%B0%E1%BB%A3ng%20m%E1%BA%B7t%20tr%E1%BB%9Di%20Anker%20PowerPort%20Solar%20IQ%2F1.jpg?alt=media&token=6f7b07f1-bda7-47e8-9662-b11f5437fb28', '2500000', '1', '2017-06-13 15:01:51', '2017-07-02 16:00:39', null, '100000', '0', '', null);
INSERT INTO `sanpham` VALUES ('37', '4', 'Canon EOS 80D 24.2MP', 'Cảm biến CMOS APS 24.2MP\r\nBộ xử lý hình ảnh DIGIC 6\r\nMàn hình cảm ứng 3.0 inch ', 'Cảm biến CMOS APS 24.2MP\r\nBộ xử lý hình ảnh DIGIC 6\r\nMàn hình cảm ứng 3.0 inch 1.04m- Dot Vari Angle\r\nQuay phim Full HD 1080p tại 60fps\r\nHệ thống lấy nét 45 điểm AF Cross-Typ\r\nISO mở rộng 25600, tốc độ chụp liên tiếp 7fps\r\nHỗ trợ kết nối Wifi và NFC\r\nCảm biến đo sáng RGB + IR 7560\r\nGiá: 26.000.000 VND', '13000000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Canon%20EOS%2080D%2024.2MP%2F1.jpg?alt=media&token=42425a35-219a-41d1-8d4d-31729312894d', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Canon%20EOS%2080D%2024.2MP%2F2.jpg?alt=media&token=1ce26b8e-f641-4edb-be93-cc0c98b1cef7', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Canon%20EOS%2080D%2024.2MP%2F4.jpg?alt=media&token=f0bf87f7-c4d5-49ac-8bf5-2920c269ba84', '17000000', '4', '2017-06-18 23:21:36', '2017-07-07 23:21:40', null, '50000', '0', '', null);
