INSERT INTO `sanpham` VALUES ('90', '14', 'Đầu Karaoke chuyên nghiệp Arirang AR-36MD', 'Đọc các loại đĩa , không kén đĩa\r\nĐầy đủ tính năng hát Midi Karaoke', 'Đọc các loại đĩa , không kén đĩa\r\nĐầy đủ tính năng hát Midi Karaoke\r\nĐọc được các loại file nén Audio và Video\r\nGiao tiếp USB\r\nTính năng chấm điểm bài hát\r\n\r\n1.490.000 VND\r\nĐầu DVD karaoke Arirang AR-36MDlà một phiên bản cao cấp nhất của hãng Arirang được hội tụ nhiều tính năng ưu việt đang được mọi người chú ý và lựa chọn nhiều trên thị trường. Với tính năng âm thanh 5.1 giúp căn phòng nhà bạn tràn ngập âm thanh trong trẻo và sinh động hơn. Ngoài ra, với cổng giao tiếp USB sẽ giúp từng thành viên trong gia đình bạn giải trí với những sở thích riêng như xem phim, nghe nhạc, xem những clip nổi tiếng trên youtobe hay những phim truyện tình lãng mạng trên các trang web nổi tiếng thông qua việc trao đổi dữ liệu thông tin trong USB với đầu DVD. Đặc biệt hơn, gia đình bạn có thể quây quần bên chiếc đầu DVD karaoke Arirang AR-36MD để cùng nhau ca hát với tính năng karaoke chấm điểm thông minh xuất hiện ngay trên màn hình giúp bạn thêm hứng thú với những buổi thi hát hấp dẫn cùng mọi người để cũng nhau giải trí sau một ngày làm việc bận rộn.\r\n\r\nĐẶC ĐIỂM NỔI BẬT\r\nTận hưởng phòng karaoke ngay tại gia đình bạn\r\nVới chiếc đầu DVD karaoke Arirang AR-36MD giúp căn phòng bạn biến thành một phòng karaoke chuyên nghiệp bởi sự tích hợp của đầu karaoke vi tính 5 số và trọn bộ đĩa karaoke Airang giúp bạn lựa chọn bài hát nhanh chóng và phong phú hơn. Ngoài ra, với chức năng karaoke chấm điểm thông minh kết hợp với tính năng hát bè, giờ đây, gia đình bạn sẽ có một không gian của một phòng karaoke hoàn hảo ngay tại nhà mà không cần tốn kém chi phí đi đến những khu vui chơi giải trí như trước.\r\nThiết kế khoa học và tinh tế\r\nChiếc đầu DVD karaoke Arirang AR-36MD được thiết kế khoa học với những nút ấn nổi, các chân cắm của micro và các cổng giao tiếp USB được sắp xếp hợp thời trang tạo cho chiếc đầu karaoke thêm hấp dẫn và bạn có thể dễ dàng thao tác, chọn bài hay kết nối với các thiết bị khác, góp phần làm không gian nội thất nhà bạn thêm trang nhã, lịch lãm và tinh tế hơn.\r\n\r\nTương thích với nhiều định dạng đĩa khác nhau\r\nChiếc đầu DVD karaoke Arirang AR-36MD hầu như có thể đọc được tất cả các loại đĩa CD, DVD không chỉ vậy với công nghệ giải mã âm thanh và hình ảnh tiên tiến đầu DVD karaoke Arirang AR-36MD còn có thể hỗ trợ đọc được các đĩa, file nén và các phiên bản định dạng khác như DVD-R, CD-RW, MP3, WMA, DivX, MP4 giúp bạn tận hưởng được nhiều thể loại chương trình khác nhau.\r\n\r\nTHÔNG SỐ KỸ THUẬT\r\n- Âm thanh 5.1\r\n- Định dạng DVD, DVD, DVD-R, CD-RW, MP3, WMA, DivX, MP4\r\n- Cổng giao tiếp USB\r\n\r\n', '4500000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/%C4%90%E1%BA%A7u%20Karaoke%20chuy%C3%AAn%20nghi%E1%BB%87p%20Arirang%20AR-36MD%2F1.jpg?alt=media&token=09fbf7cd-7c03-4597-8aaa-8ee1e4c72b46', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/%C4%90%E1%BA%A7u%20Karaoke%20chuy%C3%AAn%20nghi%E1%BB%87p%20Arirang%20AR-36MD%2Fproduct_s792.jpg?alt=media&token=4bd7375c-9590-4bff-a7da-4c8265352269', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/%C4%90%E1%BA%A7u%20Karaoke%20chuy%C3%AAn%20nghi%E1%BB%87p%20Arirang%20AR-36MD%2F%C4%90%E1%BA%A7u%20Karaoke%20chuy%C3%AAn%20nghi%E1%BB%87p%20Arirang%20AR-36MD.jpg?alt=media&token=9ad602d6-0eed-4eb2-902b-d7764d1bcf37', '7000000', '5', '2017-05-30 00:23:43', '2017-06-28 00:23:54', '4', '150000', '0', '\0', null);
INSERT INTO `sanpham` VALUES ('91', '14', 'Đầu Karaoke Wifi Acnos SK5810KTV-W', 'Phát Wifi cho thiết bị smartphone, tablet điều khiển\r\nPhát ngược video', 'Phát Wifi cho thiết bị smartphone, tablet điều khiển\r\nPhát ngược video trên smartphone,tablet cho người hát mà không cần tivi\r\nKho nhạc USB mới nhất\r\n2.439.000 VND\r\n\r\nDòng sản phẩm KTV Karaoke WiFi giá tốt, âm thanh chuyên nghiệp, tiện ích cho phòng hát và giải trí gia đình. kết hợp 2 kho nhạc: vừa MIDI vừa KTV Video. Karaoke minh họa hình theo nhạc. Tự phát WiFi cho 8 người dùng đồng thời, âm thanh SAM2695 tuyệt hảo. Cổng USB 2.0 tốc độ cao\r\nĐầu Karaoke KTV Độ Nét Cao 1080P Acnos SK5810KTV-W\r\n\r\nĐẦU MÁY TỰ PHÁT WIFI\r\n\r\nTự phát Wifi, kết nối không dây\r\n8 người điều khiển đồng thời\r\nWifi mở rộng, kết nối Wifi đầu máy với mạng Wifi có sẵn\r\nCÁC TÍNH NĂNG CÔNG NGHỆ NỔI BẬT\r\n\r\nNhạc KTV và MIDI kết hợp, số lượng bài hát nhiều nhất\r\nSAM2695 chuyên dụng, tái tạo âm thanh tuyệt hảo\r\nVideo minh hoạ hình theo nhạc, ngõ ra HDMI cho tivi LCD\r\n\r\nCHỌN BÀI THÔNG MINH\r\n\r\nApp Karaoke connect trên màn hình cảm ứng, máy tính bảng, điện thoại iOS, Android, Windows Phone\r\nRemote đa năng với phim tắt A-B-C', '2000000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/%C4%90%E1%BA%A7u%20Karaoke%20Wifi%20Acnos%20SK5810KTV-W%2F1.jpg?alt=media&token=09e0abd5-d690-4e48-b243-eadba900d141', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/%C4%90%E1%BA%A7u%20Karaoke%20Wifi%20Acnos%20SK5810KTV-W%2F2.jpg?alt=media&token=b54393b0-aa01-4376-a783-a53e22f956aa', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/%C4%90%E1%BA%A7u%20Karaoke%20Wifi%20Acnos%20SK5810KTV-W%2F2.jpg?alt=media&token=b54393b0-aa01-4376-a783-a53e22f956aa', '5000000', '2', '2017-05-28 00:23:47', '2017-07-03 15:01:51', null, '200000', '0', '', null);
