INSERT INTO `sanpham` VALUES ('138', '21', 'Canon PIXMA MX922 Wireless Color Photo Printer with Scanner, Copier and Fax', 'Giá bán:\r\n2.808.529 VNĐ\r\n\r\nThe Canon PIXMA MX922 Energy Star Wireless Inkjet Office All-in-One Printer prints in black and white at 15 ipm, in color at 10 ipm, and can output a borderless 4.0 x 6.0-inch photo in 21 seconds. Print borderless photos right at home using the 9600 x 2400 color dpi. Use the automatic double-sided print feature to print on both sides of the page and cut your paper consumption by as much as 50%. The built-in disc tray lets you design and print your own custom CDs, DVDs, and Blu-rays. Its Full HD Movie Print feature takes a frame from an HD movie recorded on a compatible EOS, PowerShot, or VIXIA camera or camcorder and prints it as a photo, ensuring you never miss a photo opportunity while shooting video. The MX922 features a scan resolution of up to 2400 x 4800 dpi optical. Plus use the built-in copy function to duplicate photos as well as documents. \r\n', 'Giá bán:\r\n2.808.529 VNĐ\r\n\r\nThe Canon PIXMA MX922 Energy Star Wireless Inkjet Office All-in-One Printer prints in black and white at 15 ipm, in color at 10 ipm, and can output a borderless 4.0 x 6.0-inch photo in 21 seconds. Print borderless photos right at home using the 9600 x 2400 color dpi. Use the automatic double-sided print feature to print on both sides of the page and cut your paper consumption by as much as 50%. The built-in disc tray lets you design and print your own custom CDs, DVDs, and Blu-rays. Its Full HD Movie Print feature takes a frame from an HD movie recorded on a compatible EOS, PowerShot, or VIXIA camera or camcorder and prints it as a photo, ensuring you never miss a photo opportunity while shooting video. The MX922 features a scan resolution of up to 2400 x 4800 dpi optical. Plus use the built-in copy function to duplicate photos as well as documents. \r\n', '29000000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Canon%20PIXMA%20MX922%20Wireless%20Color%20Photo%20Printer%20with%20Scanner%2C%20Copier%20and%20Fax%2F1.jpg?alt=media&token=d9e189cf-a616-4909-814d-80ea270f0c7a', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Canon%20PIXMA%20MX922%20Wireless%20Color%20Photo%20Printer%20with%20Scanner%2C%20Copier%20and%20Fax%2FCanon-PIXMA-MX922-Wireless-Color-Photo-Printer-with-S.jpg?alt=media&token=f63184d9-7c68-455d-855c-264ee748017c', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Canon%20PIXMA%20MX922%20Wireless%20Color%20Photo%20Printer%20with%20Scanner%2C%20Copier%20and%20Fax%2Fs-l300.jpg?alt=media&token=c481ac22-34fd-4f79-bc94-a99802fb772d', '45000000', '3', '2017-05-28 00:16:10', '2017-06-24 00:16:25', '4', '100000', '0', '\0', null);
INSERT INTO `sanpham` VALUES ('145', '22', 'Lò vi sóng Sharp RG751FVN-W 27L', 'Giá : 3.589.000 VND\r\n\r\nKhông chỉ là chiếc lò vi sóng thông thường, Sharp RG751VNW còn được tích hợp chức năng nướng, cho phép bạn dễ dàng chế biến những món ăn ưa thích thơm ngon một cách tiện lợi. Lò vi sóng Sharp RG751VNW có dung tích cực kỳ lớn, không gian bên trong thoáng rộng, điều khiển điện tử thông minh và hiện đại, bênh cạnh nhiều chương trình nấu nướng đa dạng giúp bạn nhanh chóng thực hiện những món ăn hấp dẫn chỉ trong vài phút.\r\n\r\n\r\nDung tích 25 lít\r\nVới dung tích khá lớn 25 lít, bạn có thể nướng cá hoặc gia cầm cỡ nhỏ. Khoang lò rộng bên cạnh công suất nướng lên đến 1000W, lò vi sóng Sharp RG751VNW sẽ giúp bạn có được món ăn chín đều thơm ngon thật nhanh chóng.\r\n\r\n\r\nChức năng nướng tiện lợi\r\nVới những người yêu thích món nướng, thì lò vi sóng Sharp RG751VNW sẽ là sự lựa chọn lý tưởng cho gu ẩm thực của bạn. Bạn có thể dễ dàng thực hiện các món nướng như thịt, cá, hải sản với độ chín đều, cực kỳ thơm ngon và ấn tượng. Lò sẽ mang đến thực đơn đa dạng của bạn nhất là trong dịp cuối tuần.\r\n\r\n\r\nĐa dạng chương trình nấu\r\nLò vi sóng điện tử Sharp RG751VNW có nhiều chương trình nấu cực kỳ đa dạng và phong phú. Bạn có thể nhanh chóng thực hiện các món ăn ưa thích chỉ với một nút bấm chọn chế độ nấu, ngay cả những món bắp rang, khoai tây chiên, nướng pizza cũng có mặt để làm phong phú phong cách ẩm thực của bạn.\r\n\r\n\r\nThiết kế tiện dụng dễ dùng\r\nLò vi sóng điện tử Sharp RG751VNW được thiết kế hướng đến tính tiện dụng cao. Màn hình LED giúp bạn dễ dàng quan sát và điều khiển lò theo ý muốn. Bảng điều khiển tiếng Việt giúp bạn dễ dàng làm quen ngay cả với lần đầu sử dụng. Nút bấm mở cũng được thiết kế đơn giản để bạn dễ dàng thao tác.\r\n\r\n', 'Giá : 3.589.000 VND\r\n\r\nKhông chỉ là chiếc lò vi sóng thông thường, Sharp RG751VNW còn được tích hợp chức năng nướng, cho phép bạn dễ dàng chế biến những món ăn ưa thích thơm ngon một cách tiện lợi. Lò vi só