INSERT INTO `sanpham` VALUES ('203', '55', 'Phấn Hoa Mường Lò giảm cân đẹp da', '120,000đ\r\nPHẤN HOA - GIẢM CÂN\r\nNgoài những công dụng làm đẹp giống như Sữa ong chúa, nay phấn hoa sẽ giúp chị em có một vóc dáng cân đối, quyến rũ\r\nCùng tìm hiểu cách giảm cân với phấn hoa nhé!\r\nMật ong - phấn hoa sẽ đáp ứng đẩy đủ các yêu cầu của việc giảm béo vì:\r\n- Mật ong trung hòa dịch acid ở dạ dày làm hết cảm giác đói\r\n- Cung cấp năng lượng tức thì cho cơ thể vì đường của mật ong sẽ được cơ thể hấp thụ trực tiếp không qua tiêu hóa, tránh đượt mệt mỏi\r\n- Kích thích cơ thể, giải phóng, tiêu hóa mỡ tích trữ thành năng lượng\r\n- Tăng cường trao đổi chất, tác dụng đến toàn bộ cơ thể giúp con người tươi trẻm khỏe mạnh\r\n- Phối hợp với phấn hoa sẽ cung cấp đầy đủ các chất dinh dưỡng cho cơ thể, tránh suy nhược và phát sinh bệnh khác.\r\n\r\nCÁCH DÙNG\r\n- Trước hết hãy giảm bớt khẩu phần ăn (nhất là chất bột, chất béo, bánh kẹo..) tránh ăn vặt, ăn đểm .. dần dần đến mức có thể\r\n- Pha loãng 30-50 gr mật ong với 1 lít nước uống hàng ngày vào lúc đói, mệt mỏi, trước khi ăn 30 phút\r\n- Ăn thêm 10 - 15 gr phấn hoa / ngày để bổ sung dinh dưỡng cho cơ thể\r\n- Tăng cường vận động như: đi bộ, thể dục thể thao...', '120,000đ\r\nPHẤN HOA - GIẢM CÂN\r\nNgoài những công dụng làm đẹp giống như Sữa ong chúa, nay phấn hoa sẽ giúp chị em có một vóc dáng cân đối, quyến rũ\r\nCùng tìm hiểu cách giảm cân với phấn hoa nhé!\r\nMật ong - phấn hoa sẽ đáp ứng đẩy đủ các yêu cầu của việc giảm béo vì:\r\n- Mật ong trung hòa dịch acid ở dạ dày làm hết cảm giác đói\r\n- Cung cấp năng lượng tức thì cho cơ thể vì đường của mật ong sẽ được cơ thể hấp thụ trực tiếp không qua tiêu hóa, tránh đượt mệt mỏi\r\n- Kích thích cơ thể, giải phóng, tiêu hóa mỡ tích trữ thành năng lượng\r\n- Tăng cường trao đổi chất, tác dụng đến toàn bộ cơ thể giúp con người tươi trẻm khỏe mạnh\r\n- Phối hợp với phấn hoa sẽ cung cấp đầy đủ các chất dinh dưỡng cho cơ thể, tránh suy nhược và phát sinh bệnh khác.\r\n\r\nCÁCH DÙNG\r\n- Trước hết hãy giảm bớt khẩu phần ăn (nhất là chất bột, chất béo, bánh kẹo..) tránh ăn vặt, ăn đểm .. dần dần đến mức có thể\r\n- Pha loãng 30-50 gr mật ong với 1 lít nước uống hàng ngày vào lúc đói, mệt mỏi, trước khi ăn 30 phút\r\n- Ăn thêm 10 - 15 gr phấn hoa / ngày để bổ sung dinh dưỡng cho cơ thể\r\n- Tăng cường vận động như: đi bộ, thể dục thể thao...', '55000000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Ph%E1%BA%A5n%20Hoa%20M%C6%B0%E1%BB%9Dng%20L%C3%B2%20gi%E1%BA%A3m%20c%C3%A2n%20%C4%91%E1%BA%B9p%20da%2F1.jpg?alt=media&token=1fa3085b-9567-4c81-a720-7c28cf406e9c', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Ph%E1%BA%A5n%20Hoa%20M%C6%B0%E1%BB%9Dng%20L%C3%B2%20gi%E1%BA%A3m%20c%C3%A2n%20%C4%91%E1%BA%B9p%20da%2F20130423-nan-tan-diet-cay-quy-chay-mau-duoc-lieu-0.jpg?alt=media&token=4a99dbf5-70dc-4400-8ef6-1dc2c30e5580', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Ph%E1%BA%A5n%20Hoa%20M%C6%B0%E1%BB%9Dng%20L%C3%B2%20gi%E1%BA%A3m%20c%C3%A2n%20%C4%91%E1%BA%B9p%20da%2Fmuom-muom-rang-gion.jpg?alt=media&token=803fe83d-1434-45ca-bc67-f5bbe84adcf9', '11000000', '8', '2017-05-28 10:57:04', '2017-07-05 00:16:28', null, '100000', '0', '', null);
