INSERT INTO `sanpham` VALUES ('207', '59', 'Bàn ăn xếp gọn', '3,400,000đ\r\n\r\nBàn ăn xếp gọn 4 ghế Japan 30 x 85 x 74,5 cm ( Có 3 màu : tự nhiên, trắng, nâu đen )\r\n\r\nChất liệu bền đẹp, dễ vệ sinh\r\nBàn ăn xếp gọn 4 ghế Japan 30 x 85 x 74,5 cm (Nâu gỗ tự nhiên) được làm từ gỗ cao su tự nhiên bền đẹp, chắc chắn. Chất liệu gỗ đã được xử lý chống mối mọt, chống ẩm mốc và không có mùi khó chịu. Khả năng chịu lực cao, chịu được các va đập nên không bị biến dạng, cong vênh trong quá trình sử dụng.\r\n\r\nThiết kế độc đáo, tiện dụng\r\nKhi xếp lại, bàn có kích thước 30 x 85 x 74,5 cm nên có thể sử dụng như một chiếc tủ nhỏ trưng bày bình hoa, trái cây... Các góc bàn bo tròn đảm bảo an toàn cho người sử dụng.\r\n', '3,400,000đ\r\n\r\nBàn ăn xếp gọn 4 ghế Japan 30 x 85 x 74,5 cm ( Có 3 màu : tự nhiên, trắng, nâu đen )\r\n\r\nChất liệu bền đẹp, dễ vệ sinh\r\nBàn ăn xếp gọn 4 ghế Japan 30 x 85 x 74,5 cm (Nâu gỗ tự nhiên) được làm từ gỗ cao su tự nhiên bền đẹp, chắc chắn. Chất liệu gỗ đã được xử lý chống mối mọt, chống ẩm mốc và không có mùi khó chịu. Khả năng chịu lực cao, chịu được các va đập nên không bị biến dạng, cong vênh trong quá trình sử dụng.\r\n\r\nThiết kế độc đáo, tiện dụng\r\nKhi xếp lại, bàn có kích thước 30 x 85 x 74,5 cm nên có thể sử dụng như một chiếc tủ nhỏ trưng bày bình hoa, trái cây... Các góc bàn bo tròn đảm bảo an toàn cho người sử dụng.\r\n', '29000000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/B%C3%A0n%20%C4%83n%20x%E1%BA%BFp%20g%E1%BB%8Dn%2F1.jpg?alt=media&token=54ed45b6-3810-4ac4-9eca-e578fd0c6225', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/B%C3%A0n%20%C4%83n%20x%E1%BA%BFp%20g%E1%BB%8Dn%2F2.jpg?alt=media&token=763b0c96-7a2d-4649-9cc0-32d1f65588fb', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/B%C3%A0n%20%C4%83n%20x%E1%BA%BFp%20g%E1%BB%8Dn%2F3.jpg?alt=media&token=4b50ea54-d954-4a29-81b3-b22273c6f6fb', '9000000', '1', '2017-05-30 10:57:13', '2017-07-03 15: