INSERT INTO `sanpham` VALUES ('222', '66', 'Giày tennis Nexgen NX-16187', '569.000 VND\r\n\r\nChất liệu cao cấp\r\nKiểu dáng thời trang\r\nMang tạo sự thoải mái\r\n\r\n\r\nỞ phiên bản này, Nexgen trang bị một hệ thống hỗ trợ phần giữa bàn chân và đệm gót giúp chân vững chãi và không bị lật ngay cả khi di chuyển đổi hướng bất ngờ. Gót chân cũng sẽ cảm thấy độ êm ái với miếng lót ôm lấy gan bàn chân.\r\nGiày Nexgen  NX16187 hội tụ đầy đủ các tiêu chí dưới đây:\r\nChất liệu da PU tổng hợp ở phần mặt trên của giầy được đục lỗ tạo cảm giác thoáng mát, ở 2 bên cùng phần cổ chân được tăng cường lớp đệm.\r\nMũi giày có tác dụng bảo vệ ngón chân khi di chuyển miết mũi chân xuống mặt sân. Giày rất mềm nên có thể cảm thấy tự nhiên ngay trong lần xỏ giày ra sân đầu tiên.\r\nĐế giầy được làm bằng chất liệu cao su , thiết kết với các rãnh xương cá chống trơn trượt và ặc bệt là độ bám san tốt.', '569.000 VND\r\n\r\nChất liệu cao cấp\r\nKiểu dáng thời trang\r\nMang tạo sự thoải mái\r\n\r\n\r\nỞ phiên bản này, Nexgen trang bị một hệ thống hỗ trợ phần giữa bàn chân và đệm gót giúp chân vững chãi và không bị lật ngay cả khi di chuyển đổi hướng bất ngờ. Gót chân cũng sẽ cảm thấy độ êm ái với miếng lót ôm lấy gan bàn chân.\r\nGiày Nexgen  NX16187 hội tụ đầy đủ các tiêu chí dưới đây:\r\nChất liệu da PU tổng hợp ở phần mặt trên của giầy được đục lỗ tạo cảm giác thoáng mát, ở 2 bên cùng phần cổ chân được tăng cường lớp đệm.\r\nMũi giày có tác dụng bảo vệ ngón chân khi di chuyển miết mũi chân xuống mặt sân. Giày rất mềm nên có thể cảm thấy tự nhiên ngay trong lần xỏ giày ra sân đầu tiên.\r\nĐế giầy được làm bằng chất liệu cao su , thiết kết với các rãnh xương cá chống trơn trượt và ặc bệt là độ bám san tốt.', '29000000', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Gi%C3%A0y%20tennis%20Nexgen%20NX-16187%2F1.jpg?alt=media&token=ad404d17-e55f-41f4-b48c-525bc91d3355', 'https://firebasestorage.googleapis.com/v0/b/webhinhanhnew.appspot.com/o/Gi%C3%A0y%20tennis%20Nexge